public class Test02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		System.out.println(-a);
		System.out.println (-(-a));
		a++;
		System.out.println(a);
		System.out.println(a++);//post increment after printing the increment happens
		System.out.println(a);
		int b=10;
		System.out.println(+b);
		System.out.println(++b);
		
	}

}
